import React from 'react'
import { useState, } from 'react';
import { useMapEvents,ma } from 'react-leaflet';

export default function LocationFinderDummy () {
      const [markerPositions, setMarkerPositions] = useState([]);

      const map = useMapEvents({
        click(e) {
          const newMarkerPosition = e.latlng;
          setMarkerPositions((prevPositions) => [...prevPositions, newMarkerPosition]);
          //send marker position to backend somehow
        },
      });
      return (
        <>
          {markerPositions.map((position, index) => (
            <Marker key={index} position={position} draggable={true}/>
          ))}
        </>
      );
  };
