import 'leaflet/dist/leaflet.css'
import { MapContainer, TileLayer, Marker, Popup ,useMapEvents, useMap} from 'react-leaflet';
import React, {useEffect, useState} from "react";
import L from 'leaflet';
import SlidePanel from './slidingpanel';
import Panel   from './panel';


function Map() {
  const [location, setLocation] = useState({ lat: 9.102308613438732, lng: 76.49512052536011 });
  const [markerPositions, setMarkerPositions] = useState([])
  const [boolean, setcentermarker] = useState([])
  function LocationFinderDummy () {   
      var map = useMap()
      var center = map.getCenter()

      return (
        <>
        {boolean.map((boolean,index) => {
          if (boolean) {
            <Marker key={index} position={center} draggable={true}/>
          }
        })}

        </>
      );

  };


  const handleClick = (e) => {
    e.stopPropagation()
    setcentermarker(boolean => [...boolean,true])
    console.log(boolean)
  }

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLocation({
        lat: position.coords.latitude,
        lng: position.coords.longitude,
      });
    });
  },[]);

    delete L.Icon.Default.prototype._getIconUrl;
    L.Icon.Default.mergeOptions({
    iconUrl: require('./pngegg.png'),
    iconRetinaUrl: require('./pngegg.png'),
    iconSize: new L.Point(30, 50),
    shadowUrl: null,
    shadowSize: new L.point(30,50),
    shadowAnchor: null,
    });

  return (
    <div>
    <SlidePanel handleClick={handleClick}/>
    <Panel/>
    <MapContainer style={{ height: "100vh", minHeight: "100%" }} center={[location.lat,location.lng]} zoom={13} minZoom={3} scrollWheelZoom={true} >
      <LocationFinderDummy handleClick={handleClick}/>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
    </MapContainer> 
    </div>
  );
}




export default Map;
